mapper.select:
select id, user_id, article_id, content, reply_num, like_num, create_time, update_time
from t_article_comment
where id = #{id,jdbcType=BIGINT}

select id, user_id, article_id, content, reply_num, like_num, create_time, update_time
from t_article_comment


    article_id = #{articleId}


    AND user_id = #{userId}



  order by ${orderField} ${orderType}
